	static long findNumberOfTriangles2(int[] arr,int n){
		 Arrays.sort(arr);
	        long count =0;
	       for (int i = 0; i < n-2; ++i) 
	        { 
	            int k = i + 2; 
	          for (int j = i+1; j < n; ++j) 
	            {     
	            while (k < n && arr[i] + arr[j] > arr[k]){ 
	                    ++k; 
	            }
	            if(k>j){ 
	                count += k - j - 1; 
	            }
	            }
	        }
	          return count;
	}
    static long findNumberOfTriangles(int arr[], int n)
     {
         // add your code here
    	 Arrays.sort(arr);
         int i=0,j=1,k=2,sum;
         long count =0;
         while(i<n-2){
        	 //System.out.println("i "+i+" j "+j+" k "+k);
            sum = arr[i]+arr[j];
            if(sum>=arr[k]){
                count++;
                k++;
            }else{
                j++;
                k=j+1;
            }
            if(k>=n){
                j++;
                k=j+1;
            }
            if(j>=n-1){
                i++;
                j=i+1;
                k=j+1;
            }
         }
         
           return count;
     }
